document.addEventListener("DOMContentLoaded", function() {
  const webText = document.getElementById("web-text");

  // Tableau des textes à alterner
  const texts = ["WEB", "WEB MOBILE", "REACT", "UI/UX"];
  let index = 0; // Variable pour suivre l'index actuel dans le tableau

  setInterval(() => {
    webText.classList.add("hidden"); // Cache le texte actuel

    setTimeout(() => {
      // Change le texte en fonction de l'index dans le tableau
      webText.textContent = texts[index];
      webText.classList.remove("hidden"); // Ré-affiche le texte

      // Incrémente l'index et le remet à zéro s'il dépasse la longueur du tableau
      index = (index + 1) % texts.length;
    }, 500); // Délai pour le changement après la disparition
  }, 4000); // Toutes les 4 secondes, on refait l'animation
});
